mapper.insert:
INSERT INTO BOARD (
	ID
	,SUBJECT
	,CONTENT
	,UPLOAD_FILE, UPLOAD_FILE_TWO, UPLOAD_FILE_UUID, UPLOAD_FILE_TWO_UUID) 
VALUES ( 
	BOARD_IDX_SEQ.NEXTVAL
	,#{subject}
	,#{content}
	,#{originalFileOne}
	,#{originalFileTwo}
	,#{UUIDFileOne}
	,#{UUIDFileTwo}
)

mapper.update:
UPDATE	BOARD
SET		SUBJECT = #{subject}
		,CONTENT= #{content}
WHERE	ID = #{id}
